import React, { useState } from 'react'
import './Login.css'
import {Link} from 'react-router-dom'

function Login () {

    const [username, setUserName] = useState("")
    const [password, setPassword] = useState("")

    const login = () => {
        Axios.post("http://localhost:3001/login",
            {username: username,
            password: password}).then((response) => {
                console.log(response);
            });
    };

    return (
        <div className="loginWrapper">
            <div className="login-container">
                <h1 className="login-title">Login</h1>
                <form className="form">
                    <div className="input-group"> {/* success */}
                        <label for="email">Username</label>
                        <input type="text" onChange={(e) => {setUserName(e.target.value)}} className="email" id="email"/>
                    </div>

                    <div className="input-group"> {/* error */}
                        <label for="password">Password</label>
                        <input type="password" onChange={(e) => {setPassword(e.target.value)}} className="password" id="password"/>
                    </div>

                    <button onClick={login} className="login-button">Login</button>
                </form>

                <h2>Don't have an account? Click <span><Link to="/signup">here</Link></span> to signup.</h2>
            </div>
        </div>
    )
}

export default Login;
