import React, { useState } from 'react'
import './Login.css'
import {Link} from 'react-router-dom'

function Signup () {

    const [emailReg, setEmailReg] = useState(['']);
    const [usernameReg, setUsernameReg] = useState(['']);
    const [passwordReg, setPasswordReg] = useState(['']);

    return (
        <div class="loginWrapper">
            <div class="login-container">
                <h1 class="login-title">Sign Up</h1>
                <form class="form">
                    <div class="input-group"> {/* success */}
                        <label for="email">Email</label>
                        <input type="email"  name="email" id="email" onChange={(e) => {setEmailReg(e.target.value); }}/>
                        <span class="msg">Valid email</span>
                    </div>

                    <div class="input-group"> {/* success */}
                        <label for="username">Username</label>
                        <input type="username" name="username" id="username" onChange={(e) => {setUsernameReg(e.target.value); }}/>
                    </div>

                    <div class="input-group"> {/* error */}
                        <label for="password">Password</label>
                        <input type="password" name="password" id="password" onChange={(e) => {setPasswordReg(e.target.value); }}/>
                        <span class="msg">Incorrect password</span>
                    </div>

                    <button type="submit" class="login-button">Sign Up</button>
                </form>

                <h2>Already have an account? Click <span><Link to="/login">here</Link></span> to login.</h2>
            </div>
        </div>
    )
}

export default Signup;
