import {useState, useEffect} from "react"
import axios from "axios"
import "./nba.css"

function View (urlString) {

    const [data, setData] = useState([]);
    urlString = window.location.pathname;
    var league = "nba"
    var team = ((urlString).replaceAll("%20", " ")).replace("/" + league + "/", "");

    
    
    const loadData = async () => {
        const response = await axios.get("http://localhost:3001/api/get/nbateams")
        setData(response.data);
    };

    useEffect (() => {
        loadData();
    }, []);
    
    

        data.map((item, index) => {
            if(urlString.replace("%20", " ").includes(item.Team)){
                team = item.Team;
            }
        })
    


    

        return (
            <div>
                {team}
            </div>
        );

}

export default View;
