import React, { useState } from 'react'
import {Link} from 'react-router-dom'
import Axios from 'axios'
import './Login.css'




function Signup () {

    const [emailReg, setEmailReg] = useState(['']);
    const [usernameReg, setUsernameReg] = useState(['']);
    const [passwordReg, setPasswordReg] = useState(['']);

    const register = () => {
        Axios.post('http://localhost:3001/register', {email: emailReg, username: usernameReg, password: passwordReg}).then((response) => {
            console.log(response);
        });
    };

    return (
        <div class="loginWrapper">
            <div class="login-container">
                <h1 class="login-title">Sign Up</h1>
                <form class="form">
                    <div class="input-group"> {/* success */}
                        <label for="email">Email</label>
                        <input type="text" onChange={(e) => {setEmailReg(e.target.value); }}/>
                        <span class="msg">Valid email</span>
                    </div>

                    <div class="input-group"> {/* success */}
                        <label for="username">Username</label>
                        <input type="text" onChange={(e) => {setUsernameReg(e.target.value); }}/>
                    </div>

                    <div class="input-group"> {/* error */}
                        <label for="password">Password</label>
                        <input type="password" onChange={(e) => {setPasswordReg(e.target.value); }}/>
                        <span class="msg">Incorrect password</span>
                    </div>

                    <button className="login-button" onClick={register()}>Sign Up</button>
                </form>

                <h2>Already have an account? Click <span><Link to="/login">here</Link></span> to login.</h2>
            </div>
        </div>
    )
}

export default Signup;
