import React from 'react'
import {useState, useEffect} from "react"
import axios from "axios"
import "./nba.css"

function View (urlString) {

    const [data, setData] = useState([]);
    urlString = window.location.pathname;
    var team = ((urlString).replaceAll("%20", " "));
    
    const loadData = async () => {
        const response = await axios.get("http://localhost:3001//api/get/:team")
        setData(response.data);
    };

    useEffect (() => {
        loadData();
    }, []);

    console.log(data);
        return (
            <div>
                {data.map((item, index) => {
                    return (
                        <tr key={item.index}>
                            <th scope="row">{index+1}</th>
                            <td className="teamName">{item.Team}</td>
                            <td>{item.W}</td>
                            <td>{item.L}</td>
                            <td>{item.winPCT}</td>
                        </tr>
                    )
                })}
            </div>
        );

}

export default View;
