import React, { useState } from 'react'
import './SearchBar.css'
import {UilSearch, UilMultiply} from "@iconscout/react-unicons";

function SearchBar({ placeholder, data }) {

    const [filteredData, setFilteredData] = useState([]);

    const handleFilter = (event) => {
        const searchWord = event.target.value;
        const newFilter = data.filter((value) => {
            return value.Team.toLowerCase().includes(searchWord.toLowerCase());
        });
        setFilteredData(newFilter);
    };

    return (
        <div className="search">
            <div className="searchInputs">
                <input type="text" placeholder={placeholder} data={data} onChange={handleFilter}/>
                <div className="searchIcon">
                    {filteredData.length === 0 ? <UilSearch /> : <UilMultiply id="clearSearch"/>}
                </div>
            </div>
            
            {filteredData.length !== 0 && (
                <div className="dataResult">
                    {filteredData.map((item, index) => {
                        return (
                            <a className="dataItem" href="https://www.google.com"><p>{item.Team}</p></a>
                        );
                    })}
                </div>
            )}
        </div>
    );  
}

export default SearchBar;
