import Axios from 'axios';
import React, { useEffect, useState } from 'react';

export default function Matchups() {

    Axios.defaults.withCredentials = true;
    const [role, setRole] = useState("");
    const [matchups, setMatchups] = useState([]);

    const loadMatchups = async () => {
        const response = await Axios.get("http://localhost:3001/api/get/matchups")
        setMatchups(response.data);
    };

    useEffect (() => {
        loadMatchups();
    }, []);

    useEffect(() => {
        Axios.get("http://localhost:3001/login").then((response) => {
            if(response.data.loggedIn === true){
                console.log(response.data)
                setRole(response.data.user[0].role)
            }
        })
    }, []);

    return (
        <div className="wrapper">
            <h1>{role}</h1>
            <div className="matchupsTable">
            <table className="matchups">
                        <caption>Matchups</caption>
                        <thead>
                            <tr>
                                <th>Home</th>
                                <th>Location</th>
                                <th>Away</th>
                                <th>Pick</th>
                            </tr>
                        </thead>

                        <tbody>
                        {matchups.map((item, index) => {
                            return (
                            <div className="card">
                                <tr key={item.index}>
                                    <td className="home">{item.Home}</td>
                                    <td className="away">{item.Away}</td>
                                    <td>{item.L}</td>
                                    <td>{item.winPCT}</td>
                                </tr>
                            </div>
                            )
                        })}
                        </tbody>
                    </table>
            </div>
        </div>
    );

}
